import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import PhoneInput from './components/PhoneInput';

function App() {
  const [mobileNumber, setMobileNumber] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    setMobileNumber(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('https://d3398n96t5wqx9.cloudfront.net/UsersAquisition/', {
        mobileNumber,
      });
      setMessage('Success! Your phone number has been submitted.');
    } catch (error) {
      setMessage('Error submitting phone number. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <img src="/logo.png" alt="Logo" className="logo" />
        <div className="step-indicator">STEP 1/2</div>
      </header>
      <main>
        <div className="content">
          <img src="/phone-image.png" alt="Phone" className="phone-image" />
          <h2>Enter your phone number</h2>
          <form onSubmit={handleSubmit}>
            <PhoneInput value={mobileNumber} onChange={handleInputChange} />
            <button type="submit" disabled={loading || !mobileNumber}>
              {loading ? 'Loading...' : 'CONTINUE'}
            </button>
          </form>
          {message && <p>{message}</p>}
        </div>
      </main>
      <footer>
        <p>Entertainment is a subscription service...</p>
        <a href="/terms">Terms & Conditions</a>
        <a href="/privacy">Privacy Policy</a>
      </footer>
    </div>
  );
}

export default App;
