import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import PhoneInput from './components/PhoneInput';
import { FaBars } from 'react-icons/fa'; // Importing the icon

function App() {
  const [mobileNumber, setMobileNumber] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleInputChange = (e) => {
    setMobileNumber(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('https://d3398n96t5wqx9.cloudfront.net/UsersAquisition/', {
        mobileNumber,
      });
      setMessage('Success! Your phone number has been submitted.');
    } catch (error) {
      setMessage('Error submitting phone number. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <div className="center-icons">
          <FaBars className="hamburger-icon" />
          <div className="language-dropdown">
            EN <span className="dropdown-arrow">â–¼</span>
          </div>
        </div>
      </header>
      <main>
        <div className="content">
          <div className="step-wrapper">
            <img src="/logo.png" alt="Logo" className="logo" />
            <div className="step-indicator">STEP 1/2</div>
          </div>
          <img src="/phone-image.png" alt="Phone" className="phone-image" />
          <h2>Enter your phone number</h2>
          <form onSubmit={handleSubmit}>
            <PhoneInput value={mobileNumber} onChange={handleInputChange} />
            <button type="submit" disabled={loading || !mobileNumber}>
              {loading ? 'Loading...' : 'CONTINUE'}
            </button>
          </form>
          {message && <p>{message}</p>}
        </div>
      </main>
      <footer>
        <div className="footer-content">
          <p>
            Entertainment is a subscription service that will automatically renew for 1 USD/7 Day(s). You can unsubscribe from the service at anytime, by sending STOP to **** (for operator). To make use of this service, you must be 18 or more unless you have received permission from your parents or the person who is authorized to pay your bill.
          </p>
          <div className="footer-links">
            <a href="/terms">Terms & Conditions</a>
            <a href="/privacy">Privacy Policy</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
