import React, { useState } from 'react';
import axios from 'axios';
import PhoneInput from './components/PhoneInput';
import { FaBars } from 'react-icons/fa';
import './App.css';

function App() {
  const [mobileNumber, setMobileNumber] = useState('');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleInputChange = (e) => {
    setMobileNumber(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.post('https://d3398n96t5wqx9.cloudfront.net/UsersAquisition/', {
        mobileNumber,
      });
      setMessage('Success! Your phone number has been submitted.');
    } catch (error) {
      setMessage('Error submitting phone number. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleLanguageChange = (language) => {
    console.log(`Language selected: ${language}`);
    setDropdownOpen(false);
  };

  return (
    <div className="app">
      <header className="app-header">
        <FaBars className="hamburger-icon" />
        <div className="language-dropdown-wrapper">
          <div className="language-dropdown" onClick={toggleDropdown}>
            EN <span className="dropdown-arrow">▼</span>
          </div>
          {dropdownOpen && (
            <div className="dropdown-menu">
              <div onClick={() => handleLanguageChange('EN')}>English</div>
              <div onClick={() => handleLanguageChange('ES')}>Español</div>
              <div onClick={() => handleLanguageChange('FR')}>Français</div>
              <div onClick={() => handleLanguageChange('DE')}>Deutsch</div>
            </div>
          )}
        </div>
      </header>
      <main className="main-content">
        <div className="content"><div className="step-wrapper">
          <img src="/logo.png" alt="Logo" className="logo" />
          <p className="step-indicator">STEP 1/2</p> {/* Ensure this is inside the step-wrapper */}
        </div>
          <img src="/phone-image.png" alt="Phone" className="phone-image" />


          <h2>Enter your phone number</h2>
          <form onSubmit={handleSubmit} className="phone-form">
            <PhoneInput value={mobileNumber} onChange={handleInputChange} className="phone-input" />
            <button type="submit" disabled={loading || !mobileNumber} className="submit-button">
              {loading ? 'Loading...' : 'CONTINUE'}
            </button>
          </form>
          {message && <p className="message">{message}</p>}
        </div>
      </main>
      <footer className="footer">
        <div className="footer-content">
          <p>
            Entertainment is a subscription service that will automatically renew for 1 USD/7 Day(s). You can unsubscribe from the service at anytime, by sending STOP to **** (for operator). To make use of this service, you must be 18 or more unless you have received permission from your parents or the person who is authorized to pay your bill.
          </p>
          <div className="footer-links">
            <a href="/terms">Terms & Conditions</a>
            <a href="/privacy">Privacy Policy</a>
          </div>
        </div>
      </footer>
    </div>
  );
}

export default App;
